function equating(e,t){let l=document.querySelector(`.${e}`);if(null!==l){let e,n=l.childElementCount;e=n%t!=0?Math.ceil(n/t):n/t,l.style.gridAutoFlow="column",l.style.gridTemplateRows=`repeat(${e}, auto)`,l.style.gridTemplateColumns=`repeat(${t}, 1fr)`}else console.log("element not found")}equating("advantages",2);