const modalBG=document.querySelector(".window-background"),modalCloseBtn=modalBG.querySelector(".modal__close"),toBasketButtons=document.querySelectorAll(".js-to-basket");function showModal(){modalBG.classList.add("window-background--show")}modalCloseBtn.classList.add("modal__close--show"),modalCloseBtn.addEventListener("click",(function(o){o.preventDefault(),modalBG.classList.remove("window-background--show")})),modalBG.addEventListener("click",(function(o){o.path.length<6&&modalBG.classList.remove("window-background--show")})),document.addEventListener("keydown",(function(o){o.preventDefault(),27===o.keyCode&&modalBG.classList.remove("window-background--show")})),toBasketButtons.forEach((function(o){o.addEventListener("click",(function(o){o.preventDefault(),showModal()}))}));